package Ejercicio12;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 25;
		
		System.out.println(num); //25
		num += 5;
		System.out.println(num); //30
		num -= 15;
		System.out.println(num); //15
		num *= 2;
		System.out.println(num); //30
		num /= 2;
		System.out.println(num); //15
		num %= 3;
		System.out.println(num); //0
	}

}
